mapper.select:
select chatNo, chatMsg, to_char(chatDate, 'yyyy:MM:dd:HH24:mi:ss:ff3') as chatDate, chatChk, chatDel, chatFile, reqId, resId from chat where reqId IN (#{reqId}, #{resId}) and resId IN (#{resId}, #{reqId})
and chatNo > #{lastNo}
order by chatDate

mapper.update:
update chat
	set chatChk = '0'
	where reqid = #{resId} and resid = #{reqId}
