mapper.select:
select notifyNo, reqId, resId, notifyMsg, notifyDate, notifyChk
from (
	select rownum rn, notifyNo, reqId, resId, notifyMsg, notifyDate, notifyChk
	from notification 
	where resId = #{resId}
	order by notifyNo)

	
	
		
			rn >= #{notifyNo}

mapper.update:
update notification
set notifyChk = 'true'
where notifyNo = #{notifyNo}
